        DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1 ;
        
        
        OPEN my_cursor ;
		get_test_row :LOOP
		
			FETCH my_cursor INTO  t_attend, t_month, t_year;
			IF(finished = 1) THEN 
				LEAVE get_test_row ;
			END IF ;
			 -- impossible_age_daily
