/*!50003 CREATE PROCEDURE `t_04_impAge_ts5_tc1B_columnValidation`(OUT out_success BOOL ,OUT out_test_status BOOL,OUT out_test_detail TINYTEXT)
BEGIN
	
	DECLARE t_attend VARCHAR(50) DEFAULT "";
