/*!50003 CREATE PROCEDURE `t_11_primToothExt_ts5_tc7B_columnValidation`(OUT success BOOL ,OUT test_status BOOL,OUT test_detail TINYTEXT)
BEGIN
	
	DECLARE t_attend VARCHAR(50) DEFAULT "";
