		    -- SET t_attend='';
		    -- ----------------------------------------------------------Outer if starts----------------------------------------------------------------
		    if ((t_dos != '') and (t_dos IS not NULL) and (t_attend !='') and (t_mid !='') and (t_proc_code !='') and (t_attend is not null) and (t_mid is not null) and (t_proc_code is not null)) then
