					WHERE id=t_id;
				else
					-- ----------------------------------------------- IF "G" Starts ---------------------------------------------------
					-- Checking if Full Mouth Xray D0210 is within 2 years of the date of the most current D0180 
					if ( Not(dos_recent_D0210 BETWEEN (DATE_sub(dos_recent_D0180,INTERVAL 2 YEAR)) AND (dos_recent_D0180) ) )then
