		    SELECT date_of_service FROM test_cases_2.t_18_dashboard_temp ;		    
        DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1 ;
        
         
        OPEN my_cursor ;
		get_test_row :LOOP
			FETCH my_cursor INTO   t_dos;
