			SET 	exp_reason_level=1,
				exp_ryg_status = 'green', 
				test_detail='Has no Tx code in Data Set B.'
			WHERE 	id=t_id
			; 	
		ELSE  
			set has_D2xx_D3xx =0;		
			SELECT 	COUNT(*) INTO has_D2xx_D3xx 
			FROM 	emihealth_final.`src_sealants_instead_of_filling_data_set_b` 
