DELIMITER $$

/*!50003 CREATE PROCEDURE `t_23_cbu_TS3_PL_DAILY_STATS_TABLE`()
BEGIN
DECLARE tc_success INT(1) DEFAULT 0;
	DECLARE tc_status INT(1) DEFAULT 0;
	DECLARE tc_detail VARCHAR(100) DEFAULT 'No detail';
