							SET temp_test_status=TRUE;
						END IF;
					END IF;
				END IF;
						
			END IF;
			     
			
			
			UPDATE test_cases_2.t_temp_23_ts7_tc9 
				 SET 
					expec_no_of_providers = temp_expec_no_of_providers,
					expec_total_red =temp_expec_total_red,
					expec_total_yellow=temp_expec_total_yellow, 
