        CLOSE my_cursor;
        
        SELECT COUNT(*) INTO count_total_records FROM test_cases_2.t_temp_23_ts7_tc9;
        SELECT COUNT(*) INTO count_defective_records FROM test_cases_2.t_temp_23_ts7_tc9 WHERE t_temp_23_ts7_tc9.test_status !=TRUE;
        
        
