		    SELECT id,proc_code ,attend,mid,date_of_service,tooth_no FROM test_cases_2.t_temp_24_denyPulpOnAdult_test_table ;		    
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1 ;
  
