	  `id` BIGINT(20) NOT NULL AUTO_INCREMENT,
	  `attend` VARCHAR(250) NOT NULL,
	  `date_of_service` DATETIME NOT NULL,
	  
	  `actual_procedure_count` INT(6) ZEROFILL NOT NULL,
	  `expec_procedure_count` INT(6) ZEROFILL DEFAULT NULL,
	  
