	  `test_status` BOOL DEFAULT NULL,
	  `test_detail` TINYTEXT DEFAULT NULL,
	  
	   PRIMARY KEY (`id`) 
	   
        ) ENGINE=MYISAM DEFAULT CHARSET=latin1;  
