	DECLARE t_month INT(2) DEFAULT 0;
	DECLARE t_year INT(2) DEFAULT 0;
	
	DECLARE temp_expec_no_of_providers,temp_actual_no_of_providers ,temp_count,count_total_records,count_defective_records INT DEFAULT 0;
	
