					ON 	a.`proc_code` = RIGHT(r.STATUS,5)
						AND YEAR(r.`date_of_service`) = a.`YEAR`
				HAVING  r.recovered_money != exp_recovered_money
			)AS myTab"
		       );
		     
		      prepare t from @q;
		      execute t;
		      set out_count = @out_count;
	end if;
	
END */$$
